const name='world';console.log(`Hello ${name}`);import{tns}from'tiny-slider';import date from'date-and-time';import*as data from'./videos.json';if(document.querySelector('.my-slider')){var slider=tns({container:'.my-slider',items:1,slideBy:'page',autoplay:true,speed:2500,autoplayTimeout:7000,nav:true,navPosition:"bottom",controls:false,autoplayButtonOutput:false,mode:"gallery"});}
function orderVideos(field='date',order='asc'){let result;if(field=='date'){switch(order){case'asc':result=data.sort((a,b)=>new Date(a.date).getTime()-new Date(b.date).getTime());break;default:result=data.sort((a,b)=>new Date(b.date).getTime()-new Date(a.date).getTime());}}
if(field=='title'){switch(order){case'asc':result=data.sort((a,b)=>(a.title>b.title)?1:((b.title>a.title)?-1:0))
break;default:result=data.sort((a,b)=>(b.title>a.title)?1:((a.title>b.title)?-1:0))}}
return result;}
function renderVideosBy(field='date',order='asc'){let pageVideos=document.querySelector('.new-videos');while(pageVideos.hasChildNodes()){pageVideos.removeChild(pageVideos.firstChild);}
let videos=orderVideos(field,order);let nodes=videos.map(video=>{const videoDate=new Date(video.date);const formattedDate=date.format(videoDate,'DD/MM/YYYY');let li=document.createElement('li');li.innerHTML=`<iframe width="560"height="315"src="https://www.youtube.com/embed/` + video.yt_id + `"title="YouTube video player"allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"allowfullscreen></iframe><p>`+video.title+`<time datetime="` + video.date + `">`+formattedDate+`</time></p>`;return li;});return pageVideos.append(...nodes);}
if(document.querySelector('.new-videos')){renderVideosBy('title','asc');const selectOrderElement=document.querySelector('#order-select');selectOrderElement.addEventListener('change',(event)=>{let optionValue=event.target.value;let fieldValue,orderValue="";switch(optionValue){case'titleasc':fieldValue='title';orderValue='asc';break;case'titledesc':fieldValue='title';orderValue='desc';break;case'dateasc':fieldValue='date';orderValue='asc';break;case'datedesc':fieldValue='date';orderValue='desc';break;}
renderVideosBy(fieldValue,orderValue);});}